INSERT INTO `objava` (`id`, `user_id`, `kategorija_id`, `naslov`, `vsebina`, `datum_objave`, `tip`) VALUES
(1, 1, NULL, 'ČESTITAMO - Najboljši študent UM je Iztok Fister', '<p>Univerza v Mariboru je v sodelovanju z Elektrom Maribor d. d. izvedla razpis za izbor najbolj&scaron;ega &scaron;tudenta Univerze v Mariboru.&nbsp;<br /><br />Izmed 25-ih odličnih &scaron;tudentov je komisija, ki so jo sestavljali prof. dr. Zoran Ren (Fakulteta za strojni&scaron;tvo UM), prof. dr. Mihaela Koletnik (Filozofska fakulteta UM), Nika Sajko (oddelek za izobraževanje Univerze v Mariboru) ter &scaron;tudentki Tea Markotić in Eva Senica, za naj &scaron;tudenta izbrala:&nbsp;<br /><strong><br />Iztoka Fisterja,</strong>&nbsp;&scaron;tudenta&nbsp;2. letnika magistrskega &scaron;tudijskega programa Računalni&scaron;tvo in informacijske tehnologije Fakultete za elektrotehniko, računalni&scaron;tvo in informatiko, ki trenutno pripravlja magistrsko nalogo &raquo;A comprehensive review of bat algorithms and their hybridization&laquo;, pod mentorstvom dr. Marjana Mernika ter somentorstvom profesorja iz Velike Britanije. Kako velik interes in veselje ima do raziskovalnega dela, kaže njegova bibliografija, ki trenutno obsega 15 enot. Med njimi je 8 izvirnih znanstvenih člankov, od tega so 3 dela objavljena v JRC revijah, in 5 konferenčnih člankov. Za svoje raziskovalno delo je prejel že vrsto nagrad &ndash; zmaga na konferenci ERK 2010 za najbolj&scaron;i prispevek, nagrada FERI 2010 za posebne dosežke, Perlachova nagrada 2011 za najbolj&scaron;o diplomo.</p>\r\n<p>&nbsp;</p>\r\n<p>Več informacij:<br /><span style="font-family: Arial;"><a style="color: #1c4980;" href="http://www.um.si/univerza/medijsko-sredisce/novice/Strani/novice.aspx?p=311">http://www.um.si/univerza/medijsko-sredisce/novice/Strani/novice.aspx?p=311</a></span></p>', '2013-06-19 12:16:47', 'novica'),
(2, 1, NULL, 'Objavljamo razpis za vpis v podiplomske študijske programe v študijskem letu 2013/2014', '<p><span style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;">31. 5. 2013 je bil na&nbsp;podlagi 40. člena Zakona o visokem &scaron;olstvu (Ur l. RS, &scaron;t. 32/2012), Pravilnika o razpisu za vpis in izvedbi vpisa v visokem &scaron;olstvu, sklepa Senata Univerze v Mariboru in pridobljenega soglasja Vlade Republike Slovenije objavljen&nbsp;RAZPIS ZA VPIS V PODIPLOMSKE &Scaron;TUDIJSKE PROGRAME V &Scaron;TUDIJSKEM LETU 2013/2014</span><br style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;" /><span style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;">&nbsp;</span><br style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;" /><span style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;">Več informacij najdete na spletni strani:&nbsp;</span><a style="color: #1c4980; font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;" href="http://www.feri.uni-mb.si/podrocje.aspx?id=496"><span style="font-family: Arial;">http://www.feri.uni-mb.si/podrocje.aspx?id=496</span></a></p>', '2013-06-19 12:17:23', 'novica'),
(3, 1, NULL, 'Čestitamo ekipi DORA!', '<p><strong style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;">V okviru konference Dnevi slovenske informatike je potekalo tudi tekmovanje za najbolj&scaron;i &scaron;tudentski projekt na področju informatike v letu 2013. Tričlanska komisija je za zmagovalca izbrala ekipo DORA (Doctor&rsquo;s operational research assistant). Ekipa DORA se je s svojo re&scaron;itvijo predstavila tudi na &scaron;tudentskem tekmovanju Imagine Cup 2013, kjer je zmagala v kategoriji Inovacije.&nbsp;<br /><br /><strong>DORA (Interaktiven zdravnikov asistent za zadovoljne bolnike in &scaron;e uspe&scaron;nej&scaron;e zdravnike)</strong>&nbsp;<br /><img src="http://www.feri.uni-mb.si/images/dogodki/DORATeamDSI.jpg" alt="" width="580" height="326" /><br /><br />Cilj vsake zdravstvene ustanove je optimizirati čas trajanja operacijskega posega in povečati njegovo kakovost. DORA je interaktiven zdravnikov asistent, ki omogoča edinstven prikaz informacij o bolniku pred in med operacijskimi posegi. Je produkt znanja stroke, sodobnih informacijsko komunikacijskih tehnologij ter napredne strojne opreme, ki s preprosto uporabo brezkontaktne interakcije kraj&scaron;a čas trajanja posegov ter s tem neposredno vpliva na dolžino čakalnih dob.&nbsp;<br /><br />Člani ekipe:&nbsp;<br />Bo&scaron;tjan Arzen&scaron;ek, Fakulteta za elektrotehniko, računalni&scaron;tvo in informatiko, Univerza v Mariboru.&nbsp;<br />Andraž Leitgeb, Fakulteta za elektrotehniko, računalni&scaron;tvo in informatiko, Univerza v Mariboru.&nbsp;<br />Zedin Salkanović, Fakulteta za elektrotehniko, računalni&scaron;tvo in informatiko, Univerza v Mariboru.&nbsp;<br /><br />Mentor:&nbsp;<br />Kristjan Ko&scaron;ič, Fakulteta za elektrotehniko, računalni&scaron;tvo in informatiko, Univerza v Mariboru.&nbsp;<br /><br />Zunanji sodelavec:<br />Matej Vogrinčič, Univerzitetni klinični center Maribor.&nbsp;<br /><br />Viri:&nbsp;<a style="color: #1c4980;" href="http://www.dsi2013.si/default.aspx?id=69&amp;l1=21">http://www.dsi2013.si/default.aspx?id=69&amp;l1=21</a>&nbsp;&nbsp;<br />Povezave:&nbsp;<a style="color: #1c4980;" href="http://www.dora-project.info/">http://www.dora-project.info</a>&nbsp;,&nbsp;<a style="color: #1c4980;" href="http://www.facebook.com/DoraProject">http://www.facebook.com/DoraProject</a>&nbsp;</strong></p>', '2013-06-19 12:18:35', 'novica'),
(4, 1, 1, 'Družovec Marjan - govorilne ure 26.6.2013', '<p><span style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;">V sredo 26.6.2013 bodo govorilne ure med 8:00 in 10:00.&nbsp;</span><br style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;" /><br style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;" /><span style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;">Hvala za razumevanje</span><br style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;" /><span style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;">Družovec Marjan</span></p>', '2013-06-19 12:22:48', 'oglas'),
(5, 1, 837, '	3.letnik,VS | IZREDNI ŠTUDIJ - Razvoj aplikacij za internet - VAJE', '<p><span style="font-family: tahoma, verdana, sans-serif; font-size: 12px; line-height: 16.796875px; text-align: justify;">&Scaron;tudente izrednega &scaron;tudija obve&scaron;čamo, da bodo vaje pri predmetu Razvoj aplikacij za internet (3.letnik,VS | IZREDNI &Scaron;TUDIJ) potekale v soboto, 22.06.2013 ob 08:00 v učilnici C-202.</span></p>', '2013-06-19 12:23:30', 'oglas'),
